    @import "@/styles/utils.scss";
    @import url("https://use.fontawesome.com/releases/v5.2.0/css/all.css");

	$blue : #3AA9EA;
	$yellow: #FAD03C;
	$purple: #88008E;
	$green: #28B753;

    %tooltip {
        position: relative;
        display: block;
        padding: 0;
        border: 0;
        margin: 0;
        opacity: 0;
        background: transparent;
        border-radius: 50%;
        opacity: 1;
        height: 24px;
        width: 24px;
        top: 0px;
    }

    %icon {
        background-repeat: no-repeat;
        background-size: 12px 12px;
        background-position: center;
    }

    .layer {
        display: none;

        &__active {
            display: block;
        }
    }

    .select {

    }            

    .house {
        position: relative;
        height: 100%;
        width: 100%;
        overflow: hidden;
        background: #070F25;

        &__drag {
            z-index: 0;
        }

        &__image {
	        @include bp(0,767, width){
		        width: 1397px;
            height: 1350px;
            background-size: 1397px 1350px;
				/*
				width: 100%;
				height: 100%;
		        background-size: 100% 100%;*/
		        background-repeat:no-repeat;
	        }

	        @include bp(768,5000, width){
		        position: absolute;
		        width: 1397px;
    				height: 1350px;
    				background-size: 1397px 1350px;
	        }

/*
            top: 50%;
            left: 50%;
*/


            background-image: url('../assets/house.png');
            transition: transform 100ms ease;
            transition: left 10ms ease;
            transition: top 10ms ease;
            transform-origin: 0 0;
            .img-s{
	            //max-width:120px;
	            max-height:135px;
            }

            &.modal::before {
              position: absolute;
              width: 100%;
              height: 100%;
              content: " ";
              background: rgba(0,0,0,.8);
            }

        }

        &__controls {
	        @include bp(0,767, width){
		        left: 82%;
				top: 10px;
	        }
	        @include bp(768, 5000,width){
		        right: 10px;
				top: 10px;
	        }
            position: absolute;
            font-size: 35px;
            .music-icon{
	            margin-top:45px;
	            background-size:cover;    
  		        width:35px;
  		        height:35px;
  		        display:inline-block;
	            &.play{
		            background-image: url('../assets/NEF_music_on.svg');
	            }
	            &:not(.play){
		            background-image: url('../assets/NEF_music_silent.svg');
	            }
            }
        }

        &__control {
            display: block;
            margin-top: 5px;
            color: rgba($color-light-orange, .6);
            cursor: pointer;
            transition: color 200ms ease;
            z-index: 1;

            &:hover {
                color: rgba($color-light-orange, 1);
            }
        }

        &__tooltip {
            position: absolute;
            display: block;
            padding: 0;
            border: 0;
            margin: 0;
            opacity: 0;
            background: transparent;
            border-radius: 50%;
            opacity: 1;
            height: 20px;
            width: 20px;
            top: 0px;

            &.hovered {
              z-index: 3;
            }

            &__icon {
                height: 20px;
                width: 20px;
                z-index: 1;
                color: white;
                position: absolute;
                font-size: 14px;
                background-color: #00afef;
                border-radius: 50%;
				cursor: pointer;

                &:hover{
                    z-index: 3;
                }
                &.far{
	                &::before{
		                display:inline-block;
		                position:relative;
		                left:5px;
		                top:3px;
	                }
                }
            }

            .getSet { 
            	background-image: url('../assets/icon-turn.svg'); 
            	@extend %icon; 
            	background-color:$green;
            	& + .house__popup{
	            	border-color: $green;
	            	em, .house__popup__close::before{
		            	color: $green;
	            	}
            	}
            }
		    .sealItUp { 
		    	background-image: url('../assets/icon-window.svg'); 
		    	@extend %icon; 
		    	background-color:$blue;
		    	& + .house__popup{
	            	border-color: $blue;
	            	em, .house__popup__close::before{
		            	color: $blue;
	            	}
            	}
		    }
		    .keepItClean { 
		    	background-image: url('../assets/icon-mopfluid.svg'); 
		    	@extend %icon; 
		    	background-color:$green;
		    	& + .house__popup{
	            	border-color: $green;
	            	em, .house__popup__close::before{
		            	color: $green;
	            	}
            	}
		    }
		    .lightItRight { 
		    	background-image: url('../assets/icon-light.svg'); 
		    	@extend %icon; 
		    	background-color:$yellow;
		    	& + .house__popup{
	            	border-color: $yellow;
	            	em, .house__popup__close::before{
		            	color: $yellow;
	            	}
            	}
		    }
		    .flowLow { 
		    	background-image: url('../assets/icon-water.svg'); 
		    	@extend %icon; 
		    	background-color:$blue;
		    	& + .house__popup{
	            	border-color: $blue;
	            	em, .house__popup__close::before{
		            	color: $blue;
	            	}
            	}
		    }
		    .turnItOff { 
		    	background-image: url('../assets/icon-lightening.svg'); 
		    	@extend %icon; 
		    	background-color:$purple;
		    	& + .house__popup{
	            	border-color: $purple;
	            	em, .house__popup__close::before{
		            	color: $purple;
	            	}
            	}
		    }
		    .washItWell { 
		    	background-image: url('../assets/icon-washingmachine.svg'); 
		    	@extend %icon; 
		    	background-color:$yellow;
		    	& + .house__popup{
	            	border-color: $yellow;
	            	em, .house__popup__close::before{
		            	color: $yellow;
	            	}
            	}
		    }
		    .beSmart { 
		    	background-image: url('../assets/icon-brain.svg'); 
		    	@extend %icon; 
		    	background-color:$purple;
		    	& + .house__popup{
	            	border-color: $purple;
	            	.house__popup__close::before{
			            	color: $purple;	
		            }
	            	& .house__popup__text{
		            	em{
			            	color: $purple;	
		            	}
	            	}
            	}
		    }
		    .didYouKnow { 
		    	background-image: url('../assets/icon-book.svg'); 
		    	@extend %icon; 
		    	background-color:$green;
		    	background-size: 20px 20px ;
		    	& + .house__popup{
	            	border-color: $green;
	            	.house__popup__close::before{
			            	color: $green;	
		            }
	            	& .house__popup__text{
		            	em{
			            	color: $green;	
		            	}
	            	}
            	}
		    }
        }

